/**
  * @test CHISQUARE.var_scalar_df_3
  * @brief Verify stats::chisquare::var matches scipy.stats.chi2.var for var_scalar_df_3
  */
TEST(CHISQUARE, var_scalar_df_3)
{
    Tensor<float> k({1});
    k = 3.00000000000000000;

    Tensor<float> result = stats::chisquare::var(k);

    Tensor<float> expected_result({1});
    expected_result = std::vector<float>{6.00000000000000000};

    EXPECT_NEAR(result[0], expected_result[0], 1e-06);
}

/**
  * @test CHISQUARE.var_vector_dfs
  * @brief Verify stats::chisquare::var matches scipy.stats.chi2.var for var_vector_dfs
  */
TEST(CHISQUARE, var_vector_dfs)
{
    Tensor<float> k({4});
    k = std::vector<float>{1.00000000000000000, 2.00000000000000000, 3.00000000000000000, 4.00000000000000000};

    Tensor<float> result = stats::chisquare::var(k);

    Tensor<float> expected_result({4});
    expected_result = std::vector<float>{2.00000000000000000, 4.00000000000000000, 6.00000000000000000, 8.00000000000000000};

    EXPECT_NEAR(result[0], expected_result[0], 1e-06);
    EXPECT_NEAR(result[1], expected_result[1], 1e-06);
    EXPECT_NEAR(result[2], expected_result[2], 1e-06);
    EXPECT_NEAR(result[3], expected_result[3], 1e-06);
}

